import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { ApiService } from 'src/app/shared/api.service';

@Component({
  selector: 'app-card-order',
  templateUrl: './card-order.component.html',
  styleUrls: ['./card-order.component.css']
})
export class CardOrderComponent implements OnInit {
  userdata: any;
  username: any;
  constructor(private router: Router, private api: ApiService) { }
  ngOnInit(): void {
    setTimeout(() => {
      this.router.navigate(["/"]);
      this.api.removeallitems();
    }, 4000);
    //getting item from localstorage
    let localdata = localStorage.getItem('ecomdata')
    this.userdata = localdata;
    this.username = JSON.parse(this.userdata)
  }
}
<div class="card">
    <div style="border-radius: 200px; height: 200px; width: 200px;background: #F8FAF5; margin: 0 auto;">
         <img style="width: 188px;" src ="https://cdn.pixabay.com/photo/2017/01/13/01/22/ok-1976099_1280.png">
    </div>
 <h1 style="text-align: center;color: crimson;">{{username|titlecase}} </h1>
    <h2>Your  Order has Placed Successfully</h2>
    <strong style="margin-left: 45px;">Thankyou For Shopping  With popular E-commerce</strong>

</div>
